mapper.select:
SELECT USER2_ID, USER1_ID, ROOM_ID
FROM CHAT_ROOM
WHERE ROOM_ID = #{roomId}

SELECT room_id, user1_id, user2_id
FROM CHAT_ROOM
WHERE user1_id = #{userId} OR user2_id = #{userId}

mapper.update:
UPDATE CHAT_MESSAGE
SET is_read = TRUE
WHERE room_id = #{roomId}
  AND sender_id != #{readerId}
  AND is_read = FALSE
