mapper.select:
SELECT USER_ID, USER_NAME, EMAIL
FROM USER_INFO
WHERE USER_ID = #{userId}
  AND PASSWORD = #{password}

mapper.delete:
DELETE FROM USER_TAG
WHERE user_id = #{userId}
AND tag_id IN (
SELECT t2.tag_id
FROM TAG t2
WHERE  IN (
SELECT DISTINCT 
FROM TAG t
WHERE t.tag_id IN

    #{id}

)
)
