mapper.select:
SELECT COUNT(DISTINCT U.USER_ID) as count FROM USER_INFO U
U.ADDR1 LIKE CONCAT('%', SUBSTRING(#{location}, 1, 2), '%')
AND EXISTS ( SELECT 1 FROM USER_TAG ut WHERE ut.user_id = U.USER_ID AND ut.tag_id IN
#{tagId}
)

SELECT ui.user_id AS userId, ui.user_name AS userName, ui.age AS age, ui.gender AS gender, up.profile_image_url AS profileImageUrl, up.introduction FROM USER_INFO ui LEFT JOIN USER_PROFILE up ON ui.user_id = up.user_id
ui.status = 'ACTIVE'
AND ui.ADDR1 LIKE CONCAT('%', SUBSTRING(#{location}, 1, 2), '%')
AND EXISTS ( SELECT 1 FROM USER_TAG ut WHERE ut.user_id = ui.user_id AND ut.tag_id IN
#{tagId}
)
ORDER BY ui.reg_dt DESC LIMIT #{pageSize} OFFSET #{offset}
