mapper.select:
SELECT USER_ID, USER_NAME, EMAIL
FROM USER_INFO
WHERE USER_ID = #{userId}
  AND EMAIL   = #{email}
    LIMIT 1

SELECT USER_ID, USER_NAME, EMAIL
FROM USER_INFO
WHERE USER_ID = #{userId}
  AND PASSWORD = #{password}

SELECT USER_ID, USER_NAME, EMAIL
FROM USER_INFO
WHERE 1=1

    AND USER_ID = #{userId}

AND USER_NAME = #{userName}
AND EMAIL = #{email}

mapper.update:
UPDATE USER_INFO
SET PASSWORD = #{password}
WHERE USER_ID  = #{userId}

mapper.select:
SELECT profile_image_url
FROM USER_PROFILE
